package week1.day2;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		System.out.println("----Finding Odd numbers from 1 to 10-------");
		for(int j=0; j<=i; j++) {
			if(j%2==1) {
				System.out.println(j);
			}
		}
	}

}
